import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
//import "./App.css";

import {
  Table,
  Button,
  Container,
  Modal,
  FormGroup,
} from "react-bootstrap"

//import {ModalHeader} from "reactstrap";

const user_table = [
  {
    id: 1,
    Nombre: "Esteban",
    Apellido: "Salamanca Vasquez",
    Rol: "1",
    Estado: 1,
    Fecha_creacion: "2021/01/15"
  },
  {
    id: 2, 
    Nombre: "Esteban",
    Apellido: "Salamanca Vasquez",
    Rol: "1",
    Estado: 1,
    Fecha_creacion: "2021/01/15"
  },
  {
    id: 3,
    Nombre: "Esteban",
    Apellido: "Salamanca Vasquez",
    Rol: "1",
    Estado: 1,
    Fecha_creacion: "2021/01/15"
  },
  {
    id: 4,
    Nombre: "Esteban",
    Apellido: "Salamanca Vasquez",
    Rol: "1",
    Estado: 1,
    Fecha_creacion: "2021/01/15"
  },



  ]

class Admin extends React.Component {

  state = {
    user_table: user_table,
    modalActualizar: false,
    modalInsertar: false,
    form: {
      id: "",
      Nombre: "",
      Apellido: "",
      Rol: "",
      Estado: "",
      Fecha_creacion: "",
    },
  };
  mostrarModalActualizar = (dato) => {
    this.setState({
      form: dato,
      modalActualizar: true,
    });
  };

  cerrarModalActualizar = () => {
    this.setState({ 
      modalActualizar: false });
  };

  mostrarModalInsertar = () => {
    this.setState({
      modalInsertar: true,
    });
  };

  cerrarModalInsertar = () => {
    this.setState({ modalInsertar: false });
  };

  editar = (dato) => {
    var contador = 0;
    var arreglo = this.state.user_table;
    arreglo.map((registro) => {
      if (dato.id === registro.id) {
        arreglo[contador].Nombre = dato.Nombre;
        arreglo[contador].Apellido = dato.Apellido;
        arreglo[contador].Rol = dato.Rol;
        arreglo[contador].Estado = dato.Estado;
      }
      contador++;
    });
    this.setState({ user_table: arreglo, modalActualizar: false });
  };

  eliminar = (dato) => {
    var opcion = window.confirm("EstÃ¡s Seguro que deseas Eliminar el elemento "+dato.id);
    if (opcion === true) {
      var contador = 0;
      var arreglo = this.state.user_table;
      arreglo.map((registro) => {
        if (dato.id === registro.id) {
          arreglo.splice(contador, 1);
        }
        contador++;
      });
      this.setState({ user_table: arreglo, modalActualizar: false });
    }
  };

  insertar= ()=>{
    var valorNuevo= {...this.state.form};
    valorNuevo.id=this.state.user_table.length+1;
    var lista= this.state.user_table;
    lista.push(valorNuevo);
    this.setState({ modalInsertar: false, user_table: lista });
  }

  handleChange = (e) => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };


  render() {
    return (
    <div>
        <h1 className="text-center mt-4 mb-4">Administracion de Usuarios</h1>
        <Container>
        <br />
          <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Crear</Button>
          <br />
          <br />
        <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>id</th>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Rol</th>
            <th>Estado</th>
            <th>fecha_creaciÃ³n</th>
            <th>AcciÃ³n</th>
          </tr>
        </thead>

        <tbody>
              {this.state.user_table.map((dato) => (
                <tr key={dato.id}>
                  <td>{dato.id}</td>
                  <td>{dato.Nombre}</td>
                  <td>{dato.Apellido}</td>
                  <td>{dato.Rol}</td>
                  <td>{dato.Estado}</td>
                  <td>{dato.Fecha_creacion}</td>
                  <td>
                    <Button
                      color="primary"
                      onClick={() => this.mostrarModalActualizar(dato)}

                    >
                      Editar
                    </Button>{" "}
                    <Button color="danger" onClick={()=> this.eliminar(dato)}>Eliminar</Button>
                  </td>
                </tr>
              ))}
            </tbody>

              </Table>
      </Container>

      <Modal show={this.state.modalActualizar}
        //onHide={false}
        backdrop="static"
        keyboard={false}
        >
